package com.hframework.web.config.bean;

import com.hframework.common.util.StringUtils;
import com.thoughtworks.xstream.annotations.XStreamAlias;
import com.thoughtworks.xstream.annotations.XStreamImplicit;
import java.util.List;
import com.hframework.web.config.bean.dataset.*;

/**
 * generated by hframework on 2016.
 */@XStreamAlias("dataset")
public class DataSet   {

	@XStreamAlias("module")
	private String module;
    @XStreamAlias("code")
    private String code;
    @XStreamAlias("icon")
    private String icon;
    @XStreamAlias("source")
    private String source;
	@XStreamImplicit
    @XStreamAlias("entity")
	private List<Entity> entityList;
	@XStreamAlias("name")
	private String name;
	@XStreamAlias("fields")
	private Fields fields;

    //文件编辑器用到
    @XStreamAlias("descriptor")
    private Descriptor descriptor;

    public DataSet() {
    }
   
 	 	 
     public String getModule(){
     	return module;
     }

     public void setModule(String module){
     	this.module = module;
     }
	 	 	 
     public String getCode(){
     	return code;
     }

    /**
     * 获取数据库对应的实体编码
     * @return
     */
    public String getEventObjectCode(){
        if(this.getEntityList()  == null || this.getEntityList().size() == 0) {
            return code;
        }
        if(this.getEntityList().size() == 1) {
            return StringUtils.isNotBlank(getEntityList().get(0).getText()) ? getEntityList().get(0).getText() : code;
        }else {
            return code;
        }
    }


     public void setCode(String code){
     	this.code = code;
     }
	 	 	 
     public List<Entity> getEntityList(){
     	return entityList;
     }

     public void setEntityList(List<Entity> entityList){
     	this.entityList = entityList;
     }
	 	 	 
     public String getName(){
     	return name;
     }

     public void setName(String name){
     	this.name = name;
     }
	 	 	 
     public Fields getFields(){
     	return fields;
     }

     public void setFields(Fields fields){
     	this.fields = fields;
     }

     public String getIcon() {
      return icon;
     }

     public void setIcon(String icon) {
      this.icon = icon;
     }

    public Descriptor getDescriptor() {
        return descriptor;
    }

    public void setDescriptor(Descriptor descriptor) {
        this.descriptor = descriptor;
    }

    public String getSource() {
        return source;
    }

    public void setSource(String source) {
        this.source = source;
    }
}
